int nyicilyglain(char s[5],int n){
    int ret=0, start=0;
    for(int i=0;i<99;i++){
        int id=start,tmp=0;
        for(int j=0;j<n;j++){
            if(cicilan3[i]+cicilan4[id]==s[j]) tmp++;
            id++;
        }
        if(tmp==n){
            ret=tmp;
            break;
        }
        start+=cicilan1[cicilan2[i]];
    }
    return ret;
}

int nyicilkhusus(char s[4]){
    int n=strlen(s),ret=0;
    for(int st=0;st<=n-4;st++){
        char t[4],id=0;
        for(int i=st;i<st+4;i++){
            t[id]=s[i];
            id++;
        }
        ret=nyicilyglain(t,4);
    }
    return ret;
}

int topup(char s[10], int jum,int n){
    int ret=0;
    for(int i=0;i<99;i++){
        if(cicilan3[i]==jum){
            int id=0;
            for(int j=0;j<i;j++){
                id+=cicilan1[cicilan2[j]];
            }
            int ben=0;
            for(int j=0;j<n;j++){
                if(jum+cicilan4[id]==s[j]) ben++;
                id++;
            }
            if(ben==n){
                ret=n;
                break;
            }
        }
    }
    return ret;
}


int nyicil(char s[10]){
    int n=strlen(s),ret=0,idx=0;
    if(n<5){
        return nyicilyglain(s,n);
    }

    ret=nyicilkhusus(s);

    for(int sz=5;sz<=n;sz++){
        for(int st=0;st<=n-sz;st++){
            char t[sz],id=0;
            for(int i=st;i<st+sz;i++){
                t[id]=s[i];
                id++;
            }
            int jum=0;
            for(int i=0;i<sz;i++){
                jum+=t[i];
            }
            jum/=sz;
            int tmp=topup(t,jum,sz);
            if(tmp>ret) ret=tmp;
        }
    }
    return ret;
}

int cek_cicilan(char u[10],char v[10]){
    int n=strlen(u),m=strlen(v);
    char s[6];
    int id=n-3;
    for(int i=0;i<3;i++) {
        s[i]=u[id]; id++;
    }
    id=3;
    for(int i=0;i<3;i++){
        s[id]=v[i];
        id++;
    }

    int start=0,ret=-1;
    for(int i=0;i<99;i++){
        int id=start,tmp=0;
        for(int j=0;j<6;j++){
            if(cicilan3[i]+cicilan4[id]==s[j]) tmp++;
            id++;
        }
        if(tmp==6){
            ret=128;
            break;
        }
        start+=cicilan1[cicilan2[i]];
    }
    return ret;
}

